mapper.update:

      update review
		set review_content=#{review_content}
		<if test="review_image_path!='' and review_image_path!=null">
			, review_image_path=#{review_image_path}
		</if>
		where review_id=#{review_id}

mapper.select:

SELECT AVG(review_score)
FROM review
where restaurant_license =#{restaurant_license};
